/* * $IonIdea: eqt/csi/src/java/com/equant/csi/exceptions/CSIException.java,v 1.5 2002/12/05 15:57:39 victoria.ovdenko Exp $ * * CSI: Customer Service Inventory project. * (c) Copyright 2002 Equant corporation. */package com.equant.csi.exceptions;import java.rmi.RemoteException;/** * Base CSI Exception class (extending RemoteException as NestedException) * * @author kalyan * @author Vadim Gritsenko * @author Vasyl Rublyov * @version $Revision: 1.5 $ * * @see CSIIllegalStateException */public class CSIException extends RemoteException {	private String m_detailMessage = null;		    /**     * Creates new <code>CSIException</code> without detail message.     *     */    public CSIException() {    }    /**     * Constructs an <code>CSIException</code> with the specified     * detail message.     *     * @param message the detail message.     */    public CSIException(String message) {        super(message);        m_detailMessage = message;    }    /**     * Constructs and <code>CSIException</code> with specified     * detail message and exception.     *     * @param message the detail message     * @param exception nested     *     */    public CSIException(String message, Exception exception) {        super(message, exception);        m_detailMessage = message;    }    /**     * Constructs and <code>CSIException</code> with specified     * exception.     *     * @param exception nested     *     */    public CSIException(Exception exception) {        super();        detail = exception;        m_detailMessage = (exception==null)?null:exception.getMessage();    }        public String getCSIMessage() {    	return m_detailMessage;     }}